import React, {useState} from 'react';
import { View,Text, TextInput, Button, StyleSheet, Image,Dimensions, TouchableOpacity  } from 'react-native';

const screenWidth = Dimensions.get('window').width;
const styles = StyleSheet.create({
    container: {
      flex:'1',
      alignItems: 'center',
      width:screenWidth,
    },
    input: {
      width: '80%',
      marginVertical: 10,
      padding: 10,
      borderBottomWidth: 1,
      borderColor: 'black',
    //   opacity: '0.5',
    //   borderRadius: 5,
    },
    image:{
        width: screenWidth ,
        height: '35%' ,
        resizeMode: 'cover',
        
    },
    login:{
        fontSize: '50px',
        margin: '10px',
    },

    button: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 32,
        borderRadius: 4,
        elevation: 3,
        backgroundColor: 'black',
      },
    customButton: {
        width: '100%',
        height: 50,
        backgroundColor: '#00BEDF',
        borderRadius: 15,
        justifyContent: 'center',
        alignItems: 'center',
      },
    buttonText: {
        color: 'white',
        fontSize: 18,
      },
  });
const Initial = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const handleLogin = () => {
        // Thực hiện xử lý đăng nhập ở đây
        console.log('Username:', username);
        console.log('Password:', password);
      };

    return (
        <View style={styles.container}>

            <Image
                source={require('../images/logo.jpg')} // Đường dẫn đến ảnh trong thư mục assets
                style={styles.image}
            />
            <Image
                source={require('../images/effect.png')}
                style={{width:'100%',height:50,marginTop:'0px',resizeMode:'stretch'}}

            />
            <Text style={{fontSize:70}}>
                E-wallet
            </Text>
            <View style={{width:'80%',alignItems:'flex-end',marginTop:'30px'}}>
                <TouchableOpacity style={styles.customButton} onPress={handleLogin}>
                    <Text style={styles.buttonText}>Sign Up</Text>
                </TouchableOpacity>
            </View>

            <View style={{width:'80%',alignItems:'flex-end',marginTop:'30px'}}>
                <TouchableOpacity style={styles.customButton} onPress={handleLogin}>
                    <Text style={styles.buttonText}>Login</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
};


export default Initial;
