import React, {useState} from 'react';
import { View,Text, TextInput, Button, StyleSheet, Image,Dimensions, TouchableOpacity  } from 'react-native';
const screenWidth = Dimensions.get('window').width;

const styles = StyleSheet.create({
    container: {
      flex:'1',
      alignItems: 'center',
    },
    input: {
      width: '80%',
      marginVertical: 10,
      padding: 10,
      borderBottomWidth: 1,
      borderColor: 'black',
      opacity: '0.5',
    //   borderRadius: 5,
    },
    image:{
        width: screenWidth ,
        height: screenWidth*0.8 ,
        resizeMode: 'stretch',
        
    },
    login:{
        fontSize: '50px',
        margin: '30px',
    },

    button: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingVertical: 12,
        paddingHorizontal: 32,
        borderRadius: 4,
        elevation: 3,
        backgroundColor: 'black',
      },

  });
const Login = ({ title, onPress }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const handleLogin = () => {
        // Thực hiện xử lý đăng nhập ở đây
        console.log('Username:', username);
        console.log('Password:', password);
      };

    return (
        <View style={styles.container}>
            <Image
                source={require('../images/logo.jpg')} // Đường dẫn đến ảnh trong thư mục assets
                style={styles.image}
            />
            <Text style={styles.login}>
                Login
            </Text>
            <TextInput
                style={styles.input}
                placeholder="Email"
                onChangeText={(text) => setUsername(text)}
                value={username}
            />
            <TextInput
                style={styles.input}
                placeholder="Password"
                secureTextEntry
                onChangeText={(text) => setPassword(text)}
                value={password}
            />
            <Button title="Login" onPress={handleLogin} style={styles.button} color={'black'} fontSize={'10px'} />
            <TouchableOpacity style={styles.customButton} onPress={onPress}>
                <Text style={styles.buttonText}>{title}</Text>
            </TouchableOpacity>
        </View>
    );
};


export default Login;
