import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, AsyncStorage } from 'react-native';

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
    },
    welcome: {

    },
    expense_structure: {

    },
    easy_operations: {

    },
    recent_transactions: {

    }
});

const Home = () => {
    const [screenState, setScreenState] = useState(null);

    useEffect(() => {
        const loadState = async () => {
            try {
                // Lấy trạng thái từ AsyncStorage
                const savedState = await AsyncStorage.getItem('homeScreenState');
                if (savedState !== null) {
                    // Nếu có trạng thái đã lưu, cập nhật state
                    setScreenState(JSON.parse(savedState));
                }
            } catch (error) {
                console.error('Error loading home screen state:', error);
            }
        };

        // Gọi hàm load trạng thái khi màn hình được tải lại
        loadState();
    }, []);

    useEffect(() => {
        const saveState = async () => {
            try {
                // Lưu trạng thái vào AsyncStorage
                await AsyncStorage.setItem('homeScreenState', JSON.stringify(screenState));
            } catch (error) {
                console.error('Error saving home screen state:', error);
            }
        };

        // Gọi hàm lưu trạng thái khi màn hình mất focus
        saveState();
    }, [screenState]);

    return (
        <View style={styles.container}>
            <Text>Home</Text>
        </View>
    );
};

export default Home;
